from flask import Flask, render_template, request
import random
app = Flask(__name__)
@app.route("/")
def index():
    return render_template("index.html")
@app.route("/joke")
def joke():    
    jokeList = [    
        "data:image/jpeg;base64,","https://www.boredpanda.com/blog/wp-content/uploads/2022/09/relatable-funny-memes-12-63283440474e0__700.jpg","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSuYRPJxlb-l1Pvysza8_iefH1cMJPyExXA366H46laJkMWLSqzskEL-k9LygeHIiQaHuc&usqp=CAU"]
    jokeChoice = random.choice(jokeList)
    return render_template("joke.html", jokeChoice=jokeChoice)
@app.route("/randomfacts")
def randomfacts():
    randomFacts = [
        "Pisa Kulesi'nin üzerine kurulu olduğu, daha inşaat bitmeden eğilmeye başlamasına sebep olan yumuşak toprak, kulenin 4 farklı depremden sağlam çıkmasını sağlamıştır.", "Super Mario'nun oyunda sürekli yediği mantara gerçek hayatta sinek mantarı adı veriliyor. Bu mantardan yediğinizde mikropsi adı verilen bir süreç yaşıyorsunuz, bu süreçte objeleri olduğundan daha küçük görmeye başlıyorsunuz ve bu da size etrafınızdaki objelerden daha büyükmüşsünüz hissi veriyor.","İsviçre vatandaşları isterlerse hükümeti yok sayıp, ulusal bir oylama yaptıktan sonra kendi yasalarını yaratabilirler, hatta var olan yasaları değiştirebilirler.","1929 yılı Dünya Ekonomik Bunalımı sırasında, Florida'nın küçük bir kasabası olan Quincy'de yaşayan bir iş adamı, kasabanın sakinlerini Coca Cola hissesi almaya ikna etti."]
    factChoice = random.choice(randomFacts)
    return render_template("randomfacts.html", factChoice=factChoice)
@app.route('/submit', methods=['POST'])
def submit_form():
    name = request.form['name']
    email = request.form['email']
    fact = request.form['fact']
    return render_template('form_result.html', name=name, email=email, fact=fact)


app.run()
